function swapTabElements($elm1, $elm2) {
    var $parent,
            $prev;

    $parent = $elm1.parent();
    $prev = $elm1.prev();

    if (!$prev.is($elm2)) {
        $elm1.prependTo($parent);
        $elm2.insertAfter($prev);
    } else {
        $elm1.prependTo($parent);
    }
}

